package com.ks.common;import org.springframework.web.bind.annotation.*;import javax.annotation.Resource;import java.util.List;import java.util.Map;import static javafx.scene.input.KeyCode.T;/** * HZW 2021-06-15 13:47 * 开箱即用 CURD基础操作 顶级Controller层 * @param <M> * @param <T> */public class BaseController<M extends BaseService<T>, T> {    @Resource    public M baseMapper;    /**     * 新增     * @param table     * @return     */    @PostMapping("/add")    @ResponseBody    public Dto add(@RequestBody T table) {        Dto dto = null;        try {            dto = baseMapper.add(table);        } catch (Exception e) {            dto.error("系统异常");        }        return dto;    }    @PostMapping("/delete")    @ResponseBody    public Dto delete(@RequestParam("ids[]") List<Integer> ids) {        Dto dto = null;        try {            dto = baseMapper.delete(ids);        } catch (Exception e) {            dto.error("系统异常");        }        return dto;    }    @PostMapping("/update")    @ResponseBody    public Dto update(@RequestBody T table) {        Dto dto = null;        try {            dto = baseMapper.update(table);        } catch (Exception e) {            dto.error("系统异常");        }        return dto;    }    @RequestMapping(value = "/lists", method = RequestMethod.GET)    @ResponseBody    public Dto findByCondition() {        Dto dto = null;        try {            dto = baseMapper.findByCondition();        } catch (Exception e) {            dto.error("系统异常");        }        return dto;    }    @RequestMapping(value = "/view", method = RequestMethod.GET)    @ResponseBody    public Dto findById(Integer id) {        Dto dto = null;        try {            dto = baseMapper.findById(id);        } catch (Exception e) {            dto.error("系统异常");        }        return dto;    }}