package com.ks.common;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.core.mapper.Mapper;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import com.ks.pojo.User;import org.springframework.transaction.annotation.Transactional;import javax.annotation.Resource;import java.util.List;public class BaseServiceImpl<M extends BaseMapper<T>, T extends BaseEntity>  implements BaseService<T> {    @Resource    public M mapper;    @Transactional(rollbackFor = Exception.class)    @Override    public Dto add(T t) {        if (mapper.insert(t)>0){            return Dto.success();        }else {            return Dto.success("新增失败");        }    }    @Transactional(rollbackFor = Exception.class)    @Override    public Dto update(T t) {        if (mapper.updateById(t)>0){            return Dto.success();        }else {            return Dto.success("新增失败");        }    }    @Transactional(rollbackFor = Exception.class)    @Override    public Dto delete(List<Integer> ids) {        mapper.deleteBatchIds(ids);        return Dto.success("删除成功");    }    @Override    public Dto findByCondition() {        return Dto.success("查询成功",mapper.selectList(null));    }    @Override    public Dto findById(Integer id) {        if (id == null || id == 0) {            return Dto.error("记录ID不能为空");        }        return Dto.success("查询成功",mapper.selectById(id));    }}