package com.ks.common;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import java.util.List;public class BaseServiceImpl<M extends BaseMapper<T>, T extends BaseEntity> extends ServiceImpl<M, T>  implements BaseService<T> {    @Override    public Dto add(T t) {        if (this.save(t)){            return Dto.success();        }else {            return Dto.success("新增失败");        }    }    @Override    public Dto update(T t) {        if (this.updateById(t)){            return Dto.success();        }else {            return Dto.success("新增失败");        }    }    @Override    public Dto delete(List<Integer> ids) {        this.removeByIds(ids);        return Dto.success("删除成功");    }    @Override    public Dto findByCondition() {        return Dto.success("查询成功",this.listByIds(null));    }    @Override    public Dto findById(Integer id) {        if (id == null || id == 0) {            return Dto.error("记录ID不能为空");        }        return Dto.success("查询成功",this.getById(id));    }}