package com.ks.common;import lombok.Data;import org.springframework.http.HttpStatus;import java.io.Serializable;/** * HZW 2021-06-18 13:47 * 顶级Dto 返回JSON层 */@Datapublic class Dto implements Serializable {    /**     * 返回具体的数据内容（pojo/自定义的VO/其他内容）     */    private Object data;    /**     * 错误码，自定义     */    private Integer code=200;    /**     * 对应的提示信息     */    private String msg="操作成功";    /**     * 构造函数     */    public Dto() {    }    public Dto(String msg) {        this.msg = msg;    }    public Dto(Object data) {        this.data = data;    }    public Dto(Integer code, String msg) {        this.code = code;        this.msg = msg;    }    public Dto(Integer code, String msg, Object data) {        this.code = code;        this.msg = msg;        this.data = data;    }    public static Dto success() {        return new Dto();    }    public static Dto success(String msg) {        return new Dto(msg);    }    public static Dto success(String msg, Object data) {        return new Dto(200, msg, data);    }    public static Dto error() {        return new Dto(500, "操作失败!");    }    public static Dto error(String msg) {        return new Dto(500, msg);    }    public static Dto error(Integer code, String msg) {        return new Dto(code, msg);    }    public static Dto error(Integer code, String msg, Object data) {        return new Dto(code, msg, data);    }    public static Dto error(HttpStatus httpStatus, String msg, Object data) {        return new Dto(httpStatus.value(), msg, data);    }    public Object error(HttpStatus httpStatus, String msg) {        this.code = httpStatus.value();        this.msg = msg;        return this;    }}