package com.Ks.Base;import com.Ks.Mapper.ksMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.transaction.annotation.Transactional;import java.util.List;public   class BaseServiceImpl<M extends ksMapper<T>, T extends BaseEntity>  implements BaseService<T> {    @Autowired    protected M mapper;    @Transactional(rollbackFor = Exception.class)    @Override    public Dto adds(List<T> t) {        mapper.insertBatch(t);        return Dto.OKmag("新增成功");    }    @Transactional(rollbackFor = Exception.class)    @Override    public Dto add(T t) {       mapper.insert(t);       return Dto.OKmag("新增成功");    }    @Transactional(rollbackFor = Exception.class)    @Override    public Dto update(T t) {        mapper.updateById(t);        return Dto.OKmag("修改成功");    }    @Transactional(rollbackFor = Exception.class)    @Override    public Dto delete(List<Integer> ids) {        mapper.deleteBatchIds(ids);        return Dto.OKmag("删除成功");    }    @Override    public List<T> findByCondition() {        return mapper.selectList(null);    }    @Override    public List<T> findById(List<Integer> ids) {        return mapper.selectBatchIds(ids);    }}