package com.ks.Server.Impl;import com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;import com.ks.Server.UserSrever;import com.ks.common.Base.BaseServiceImpl;import com.ks.dao.UserDao;import com.ks.common.pojo.User;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * HZW 2021-06-18 15:46 */@Servicepublic class UserSreverImpl extends BaseServiceImpl<UserDao,User> implements UserSrever{    @Autowired    private UserDao userDao;    @Override    public User getUser(String name) {        QueryWrapper<User> queryWrapper = new QueryWrapper<>();        queryWrapper                .eq("name", name);        return userDao.selectOne(queryWrapper);    }}