package com.ks.common.Base;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.springframework.transaction.annotation.Transactional;import javax.annotation.Resource;import java.util.List;public class BaseServiceImpl<M extends BaseMapper<T>, T extends BaseEntity>  implements BaseService<T> {    @Resource    public M mapper;    @Transactional(rollbackFor = Exception.class)    @Override    public Dto add(T t) {       mapper.insert(t);       return Dto.OKmag("新增成功");    }    @Transactional(rollbackFor = Exception.class)    @Override    public Dto update(T t) {        mapper.updateById(t);        return Dto.OKmag("修改成功");    }    @Transactional(rollbackFor = Exception.class)    @Override    public Dto delete(List<Integer> ids) {        mapper.deleteBatchIds(ids);        return Dto.OKmag("删除成功");    }    @Override    public Dto findByCondition() {        return Dto.OKmag(mapper.selectList(null),"查询成功");    }    @Override    public Dto findById(List<Integer> ids) {        return Dto.OKmag(mapper.selectBatchIds(ids),"查询成功");    }}