package com.ks.common.Base;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import net.bytebuddy.asm.Advice;import org.springframework.http.HttpStatus;import java.io.Serializable;import java.util.HashMap;import java.util.Map;/** * HZW 2021-06-18 13:47 * 顶级Dto 返回JSON层 */@Data@Builderpublic class Dto implements Serializable {    /**     * 返回具体的数据内容（pojo/自定义的VO/其他内容）     */    private Object data;    /**     * 错误码，自定义     */    private Integer code;    /**     * 对应的提示信息     */    private String msg;    /**     *成功操作 状态码     */    public static final Dto OK =Dto.builder().msg("操作成功").code(200).build();    public static final Dto OERROR=Dto.builder().msg("操作异常").code(500).build();    public static final Dto SYSTEMERROR =Dto.builder().msg("系统异常").code(600).build();    public static Dto OKmag(String msg) {        return Dto.builder().msg(msg).code(200).build();    }    public static Dto OKmag(Object data) {        return Dto.builder().data(data).msg("操作成功").code(200).build();    }    public static Dto OKmag(Object data,String msg) {        return Dto.builder().data(data).msg(msg).code(200).build();    }    public static Dto error(String msg) {        return Dto.builder().msg(msg).code(500).build();    }    public static Dto errorsystem(String msg) {        return Dto.builder().msg(msg).code(600).build();    }}