package com.ks.common.Utils.Base;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;/** * HZW 2021-06-15 13:47 * CURD基础操作 顶级Controller层 * @param <M> * @param <T> */public class BaseController<M extends BaseService<T>, T> {    @Autowired    public M baseMapper;    /**     * 新增     * @param table     * @return     */    @PostMapping("/add")    @ResponseBody    public Dto add(@RequestBody T table) {        try {             return baseMapper.add(table);        } catch (Exception e) {           return Dto.errorsystem("系统异常");        }    }    @PostMapping("/delete")    @ResponseBody    public Dto delete(@RequestParam("ids[]") List<Integer> ids) {        try {           return  baseMapper.delete(ids);        } catch (Exception e) {           return Dto.errorsystem("系统异常");        }    }    @PostMapping("/update")    @ResponseBody    public Dto update(@RequestBody T table) {        try {            return baseMapper.update(table);        } catch (Exception e) {           return Dto.errorsystem("系统异常");        }    }    @GetMapping("/lists")    @ResponseBody    public Dto findByCondition() {        try {            return Dto.OKdata(baseMapper.findByCondition(),"查询成功");        } catch (Exception e) {            return Dto.errorsystem("系统异常");        }    }    @GetMapping("/view")    @ResponseBody    public Dto findById(List<Integer> ids) {        if (ids== null) {            return Dto.error("记录ID不能为空");        }        try {            return Dto.OKdata(baseMapper.findById(ids),"查询成功");        } catch (Exception e) {            return Dto.errorsystem("系统异常");        }    }}